import React, { useEffect, useRef } from 'react';
import * as Highcharts from "highcharts";

const OnePlayer: React.FC = () => {
    const chartContainer = useRef<HTMLDivElement>(null);

    useEffect(() => {
        // Charger les scripts Highcharts
        const highchartsScript = document.createElement('script');
        highchartsScript.src = 'https://code.highcharts.com/highcharts.js';
        highchartsScript.onload = () => {
            const highchartsModules = [
                'data.js',
                'drilldown.js',
                'exporting.js',
                'export-data.js',
                'accessibility.js'
            ];
            highchartsModules.forEach(module => {
                const moduleScript = document.createElement('script');
                moduleScript.src = `https://code.highcharts.com/modules/${module}`;
                document.head.appendChild(moduleScript);
            });

            // Initialiser le graphique une fois que Highcharts est chargé
            if (chartContainer.current) {
                Highcharts.chart(chartContainer.current, {
                    title: {
                        text: "Partie monojoueur",
                        style: {
                            color: 'white',
                            fontSize: '30px'
                        }
                    },
                    credits: {
                        enabled: false
                    },
                    chart: {
                        type: 'networkgraph',
                        backgroundColor: null,
                    },
                    plotOptions: {
                        networkgraph: {
                            draggable: true,
                            layoutAlgorithm: {
                                enableSimulation: true
                            }
                        }
                    },
                    series: [{
                        data: [{
                            from: 'startWord',
                            to: 'endWord'
                        }, {
                            from: 'endWord',
                            to: 'startWord'
                        }]
                    }]
                });
            }
        };
        document.head.appendChild(highchartsScript);

        return () => {
            // Nettoyer les scripts lorsque le composant est démonté
            document.head.removeChild(highchartsScript);
        };
    }, []);

    return (
        <div>
            <div ref={chartContainer}></div>
            <form method="POST">
                <div className="word-area">
                    <input type='text' name='user_word' />
                    <input type="submit" id="play" name="play" value="Jouer" />
                </div>
            </form>
            <br />
            <p><a href="home.php">Retour</a></p>
        </div>
    );
};

export default OnePlayer;
