import React from 'react';
import { useState , useEffect } from 'react';
import './App.css';
import './style/header.css';
import Connexion from './frontend/Connexion';
import Inscription from './frontend/Inscription';
import User from './class/User';

const App = () => {
  const [showConnexion, setShowConnexion] = useState(true);

  const handleToggle = () => {
    setShowConnexion(!showConnexion);
  };

  return (
    <div>
      {showConnexion ? <Connexion /> : <Inscription />}
      <button onClick={handleToggle}>
        {showConnexion ? "S'inscrire" : "Se connecter"}
      </button>
    </div>
  );
};

export default App;

export default App;
