import React, { useEffect, useRef } from 'react';
import * as Highcharts from "highcharts";

const OnePlayer: React.FC = () => {
    const chartContainer = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (chartContainer.current) {
            // Définir l'objet de configuration pour le graphique Highcharts
            const options: Highcharts.Options = {
                title: {
                    text: "Partie monojoueur",
                    style: {
                        color: 'white',
                        fontSize: '30px'
                    }
                },
                credits: {
                    enabled: false
                },
                chart: {
                    type: 'networkgraph',
                    renderTo: chartContainer.current // Utilisation de renderTo pour définir l'emplacement du graphique
                },
                plotOptions: {
                    networkgraph: {
                        draggable: true,
                        layoutAlgorithm: {
                            enableSimulation: true
                        }
                    }
                },
                series: [{
                    data: [{
                        from: 'startWord',
                        to: 'endWord'
                    }, {
                        from: 'endWord',
                        to: 'startWord'
                    }]
                }]
            };

            // Initialiser le graphique avec Highcharts.chart
            Highcharts.chart(options);
        }
    }, []);

    return (
        <div>
            <div ref={chartContainer}></div>
            <form method="POST">
                <div className="word-area">
                    <input type='text' name='user_word' />
                    <input type="submit" id="play" name="play" value="Jouer" />
                </div>
            </form>
            <br />
            <p><a href="home.php">Retour</a></p>
        </div>
    );
};

export default OnePlayer;
