import React, { useState } from 'react';

function Connexion(){
    const [pseudo, setPseudo] = useState('');
    const [mdp, setMdp] = useState('');

    return(
        <>
           <div className="container">
                <form method="post">
                    <h1>Connexion</h1>
                    <div className="input">
                        <input type="text" onChange={ (event) => setPseudo(event.target.value) } value={ pseudo } name="user" id="user" placeholder="Nom d'utilisateur" required />
                    </div>
                    <div className="input">
                        <input type="password" onChange={ (event) => setPseudo(event.target.value) } value={ mdp } name="pwd" id="pwd" placeholder="Mot de passe" required />
                        <p><a href="../backend/forgotPwd.php">Mot de passe oubli√© ?</a></p>
                    </div>
                    <input type="submit" id="submit" value="SE CONNECTER" />
                    <div className="options">
                        <p>Vous n'avez pas de compte ? <a href="../backend/signup.php">S'inscrire</a></p>
                    </div>
                </form>
            </div>
        </>
    );
};

export default Connexion;
