import React, { useEffect, useRef } from 'react';
import * as Highcharts from "highcharts";

import Exporting from 'highcharts/modules/exporting';
Exporting(Highcharts);

const OnePlayer: React.FC = () => {
    const chartContainer = useRef<HTMLDivElement>(null);

    // Load the exporting module.

    // Initialize exporting module.
    let data = [{
                from: 'startWord',
                to: 'endWord'
            }, {
                from: 'endWord',
                to: 'startWord'
            }];

    // Generate the chart
    Highcharts.chart('container', {
        title: {
            text: "Partie monojoueur",
            style: {
                color: 'white',
                fontSize: '30px'
            }
        },

        credits: {
            enabled: false
        },

        chart: {
            type: 'networkgraph',
            backgroundColor: null,
        },

        plotOptions: {
            networkgraph: {
                draggable: true,
                layoutAlgorithm: {
                    enableSimulation: true
                }
            }
        },
        series: [{
            dataLabels: {
                enabled: true,
                linkTextPath: {
                    attributes: {
                        dy: 15
                    }
                },
                // linkFormat: '{point.fromNode.name} \u2192 {point.toNode.name}',
                linkFormat: '{point.linkFormat}',
                textPath: {
                    enabled: true,
                    attributes: {
                        dy: -5,
                        startOffset: '50%',
                        // textLength: 1000
                    }
                },
                format: '{point.name}',
                style: {
                    color: 'white',
                    fontSize: '15px',
                    letterSpacing: '0px',   
                    textOutline: null
                }
            },
            marker: {
                radius: 20
            },
            // data: [{
            //     from: startWord,
            //     to: endWord
            // }, {
            //     from: endWord,
            //     to: startWord
            // }]
            data : data
        }]
    });

    return (
        <div>
            <div ref={chartContainer}></div>
            <form method="POST">
                <div className="word-area">
                    <input type='text' name='user_word' />
                    <input type="submit" id="play" name="play" value="Jouer" />
                </div>
            </form>
            <br />
            <p><a href="home.php">Retour</a></p>
        </div>
    );
};

export default OnePlayer;
