import React, { useEffect } from 'react';

const OnePlayer: React.FC = () => {

    useEffect(() => {
        // Créer une balise de script
        const script = document.createElement('script');
        script.src = '../script/graphic.js';
        script.async = true;

        // Ajouter le script au corps du document
        document.body.appendChild(script);

        // Nettoyer le script lorsque le composant est démonté
        return () => {
          document.body.removeChild(script);
        };
      }, []);

  return (
    <div>
      <div id="graph-container"></div>
      <form method="POST">
        <div className="word-area">
          <input type='text' name='user_word' />
          <input type="submit" id="play" name="play" value="Jouer" />
        </div>
      </form>
      <br />
      <p><a href="home.php">Retour</a></p>
    </div>
  );
};

export default OnePlayer;
