import React, { useEffect, useRef } from 'react';
import * as Highcharts from "highcharts";

import Exporting from 'highcharts/modules/exporting';
Exporting(Highcharts);


const OnePlayer: React.FC = () => {
    const chartContainer = useRef<HTMLDivElement>(null);

    // Initialize exporting module.

    useEffect(() => {
        // Définir l'objet de configuration pour le graphique Highcharts
        const options: Highcharts.Options = {
            title: {
                text: "Partie monojoueur",
                style: {
                    color: 'white',
                    fontSize: '30px'
                }
            },
            credits: {
                enabled: false
            },
            chart: {
                type: 'networkgraph',
                backgroundColor: null,
                renderTo: chartContainer.current // Utilisation de renderTo pour définir l'emplacement du graphique
            },
            plotOptions: {
                networkgraph: {
                    draggable: true,
                    layoutAlgorithm: {
                        enableSimulation: true
                    }
                }
            },
            series: [{
                dataLabels: {
                    enabled: true,
                    linkTextPath: {
                        attributes: {
                            dy: 15
                        }
                    },
                    linkFormat: '{point.linkFormat}',
                    textPath: {
                        enabled: true,
                        attributes: {
                            dy: -5,
                            startOffset: '50%',
                        }
                    },
                    format: '{point.name}',
                    style: {
                        color: 'white',
                        fontSize: '15px',
                        letterSpacing: '0px',   
                        textOutline: null
                    }
                },
                marker: {
                    radius: 20
                },
                data: [{
                    from: 'startWord',
                    to: 'endWord'
                }, {
                    from: 'endWord',
                    to: 'startWord'
                }]
            }]
        };

        // Initialiser le graphique avec Highcharts.chart et l'objet de configuration
        Highcharts.chart(options);

    }, []);

    return (
        <div>
            <div ref={chartContainer}></div>
            <form method="POST">
                <div className="word-area">
                    <input type='text' name='user_word' />
                    <input type="submit" id="play" name="play" value="Jouer" />
                </div>
            </form>
            <br />
            <p><a href="home.php">Retour</a></p>
        </div>
    );
};

export default OnePlayer;
